INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98404,'Vitt pulver som används som konserveringsmedel mot <a href=''../../uk/bact/clbot.htm''>Clostridium botulinum</a> (en bakterie som kan orsaka botulism) i köttprodukter.''Natriumnitrit','E250','Naturligt förekommande mineral. Kan brytas eller produceras kemiskt från natriumnitrat. Nitriter är prekursorer av nitrosaminer (som kan vara cancerframkallande), som bildas i magen från nitriter och proteiner. Höga halter kan reagera med hemoglobin. Nitrit är inte tillåtna i produkter avsedda för barn under 6 månaders ålder. Välkända cancerframkallande, nitrosaminer, form vid upphettning över öppen eld.','', 'Nitrit är prekursorer av (möjligen cancerframkallande) nitrosaminer, som bildas i magsäcken från nitrit och proteiner. Höga halter kan reagera med hemoglobin. Det är inte tillåtet i produkter avsedda för barn under 6 månader. Små barn har en annan typ av hemoglobin, som är mycket mer reaktiv gentemot nitrit än normalt hemoglobin. ','f', 2, 52,'allergi, astma, migrän, cancer', 't', '', '', 'Sodium nitrite', 'f', '', 'f', 'f', 'f');
INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98405, 'Vitt pulver används som ett konserveringsmedel och mot fädning av naturliga färger.', 'Natriumnitrat','E251','naturligt förekommande mineral. Också i nästan alla grönsaker. Vid uppvärmning eller passage till magsäcken förvandlas till nitrit (E250)','', 'Nitrat har inga biverkningar, men kan de omvandlas till nitrit (<a href=''e250.htm''>E250</a>) antingen genom upphettning eller när den förekommer i magen. ','f ', 1, 10,'allergi, astma, migrän ', 't', '', '', 'Sodium nitrate', 'f', 'Chilesalpeter, kubisk eller soda salpeter', 'f', 'f', 'f');
