INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98407, 'Ättiksyra används som ett konserveringsmedel mot bakterier och svampar. I majonnäs är det tillsätts för att öka inaktiveringen av <a href=''../../uk/bact/salmon.htm''>Salmonella</a>. Den högsta aktiviteten är vid lågt pH. Den kan också användas som en buffert i sura livsmedel. Den används också som en doft komponent.','Ättiksyra','E260','Natural syra, som finns i de flesta frukter. Producerad av bakteriell jäsning och därmed i alla jästa produkter. Kommersiellt framställs genom bakteriell jäsning av socker, melass eller alkohol eller genom kemisk syntes från acetealdehyde. ','','Inga biverkningar, det är en normal komponent i alla kroppens celler. Bör endast undvikas av människor som har en (mycket sällsynt) vinäger intolerans.', 'f', 0, 82, '', 't', '', '', 'Acetic acid', 'f', 'Ethanoic acid, Glacial', 'f', 'f', 'f');
INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98408, 'Acetates are used as preservatives and buffers.', 'Potassium acetate', 'E261', 'Potassium salt of acetic acid, an natural acid, present in most fruits. Produced by bacterial fermentation and thus present in all fermented products. Commercially produced by bacterial fermentation of sugar, molasses or alcohol or by chemical synthesis from acetealdehyde. Should be avoided the people who can not tolerate vinegar.', '', 'No side effects, acetates are a normal component of all body cells. Should only be avoided by people who have a (very rare) vinegar intolerance.', 'f', 0, 0, '', 't', '', '', 'Potassium acetate', 'f', '', 'f', 'f', 'f');
