INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98525, 'Stabilisator, lågt kaloriinnehåll sötningsmedel, bulkmedel, etc.','Sorbitol','E420','En naturlig kolhydrat alkohol i många bär och frukter, t.ex. äpplen, plommon, körsbär och vindruvor. Det är kommersiellt framställt av glukos (dextros). Sorbitol delvis absorberas och används i kroppen'' s metaboliska processer som fruktos. Resten av fermentationen sker i kolon. Gas frigörs under fermentationen kan orsaka ansamling av gas och buk. Intoleranta människor kan möta laxerande effekt. . Förbjudet för barn ','','är sorbitol delvis absorberas och metaboliseras i fruktos av kroppen, den återstående fraktionen fermenteras i tjocktarmen. Under jäsningen, är gaser som produceras, vilket kan orsaka uppsvälldhet och flatulens. I intoleranta personer kan fungera som ett laxermedel. Det är normalt inga biverkningar i de använda koncentrationerna, men en del intoleranta människor visar gasbildning vid ett intag så låg som 5 gram sorbitol. Biverkningar inträffar normalt efter ett intag av 25-30 gram i en enda dos, vilket är långt över användning i normala livsmedel.','f', 1, 19,'diarré, flatulens, uppsvälld buk', 't', '', '', 'Sorbitol', 't', 'D-glucitol, sorbitol, Sorbol, D-sorbitol, D-glucitol sirap, sorbitolsirap', 'f', 'f', 'f');
INSERT INTO e (id, function, name, number, origin, properties, sideeffect, vegan, category, productcount, diseases, approved, linksbanned, linksdiseases, nameenglish, gmo, aliases, bannedinaustralia, bannedincanada, bannedinusa) VALUES(98528, 'Klumpförebyggande medel, lågt kaloriinnehåll sötningsmedel, bulkmedel, etc.','Mannitol','E421','En naturlig kolhydrat alkohol förekommer i många växter, t.ex. barrträd, sjögräs och svamp. Det är kommersiellt framställt av glukos (dextros). Mannitol är till stor del absorberas och används av kroppen'' s metaboliska processer som glukos. Resten av fermentationen sker i kolon. Gas frigörs under fermentationen kan orsaka ansamling av gas och buk. Intoleranta människor kan möta laxerande effekt. Förbjudet för barn. ','','Är mannitol stor del absorberas och metaboliseras som glukos i kroppen. Den återstående fraktionen fermenteras i grovtarmen. Under jäsningen, är gaser som produceras, vilket kan orsaka uppsvälldhet och flatulens. I intoleranta personer kan fungera som ett laxermedel. Biverkningar inträffar normalt efter ett intag av 25-30 gram i en enda dos, vilket är långt över användning i normala livsmedel.','f', 1, 7,'diarré, flatulens, uppsvälld buk', 't', '', '', 'Mannitol', 't', '', 'f', 'f', 'f');
