INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98321,'vegetabilisk svart','t','f','f','t',3, null, 'Svart, vatten olöslig färg. Används även som en filtrering medel.','f', null, null, 'Vegetabiliskt kol','Vegetable carbon','E153','Naturligt inslag, som produceras genom förbränning av vegetabiliskt material.', null, 'Inga biverkningar är kända i de använda koncentrationer. Används medicinskt som ett anti-diarrémedel.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98322, null, 't','t','t','t',3,'allergi, astma, mutagenicity','Brun mat färg. Mycket lösligt i vatten.','f', null, null, 'Brown FK','Brown FK','E154','Blandning av syntetiska azofärgämnen. Kan framkalla intolerans hos människor intoleranta mot salicylater. Dessutom släpper den histamin och kan förstärka symptom på astma. Tillsammans med bensoater, orsakar hyperaktivitet hos barn. En av komponenterna i blandningen kan vara mutagena.', null, 'Eftersom det är en azofärgämne, kan det framkalla intolerans hos människor intoleranta mot salicylater. Dessutom är det en histamin befriare, och kan förstärka symtom på astma. I kombination med bensoater, är det också implicerats i hyperaktivitet hos barn. Några av föreningarna i blandningen kan vara mutagena, varför användningen är begränsad till ett fåtal produkter.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98323,'Chokladbrun HT, CI (1975) No. 20285','t','f','t','t',3,'allergi, astma, hyperaktivitet','Brun mat färg. Mycket lösligt i vatten.','f', null, null, 'Brown HT','Brown HT','E155','Synthetic azofärgämne. Kan framkalla intolerans hos människor intoleranta mot salicylater. Dessutom släpper den histamin och kan förstärka symptom på astma. Tillsammans med bensoater orsakar hyperaktivitet hos barn.', null, 'Eftersom det är en azofärgämne, kan det framkalla intolerans hos människor intoleranta mot salicylater. Dessutom är det en histamin befriare, och kan förstärka symtom på astma. I kombination med bensoater, är det också inblandat i hyperaktivitet hos barn.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98325,'CI (1975) nr 40.800, Blandade karotener, betakaroten, karotener-naturliga, CI (1975) nr 75.130, Natural beta-karoten, beta-karoten från Blakeslea trispora, karotener (alger), karotener (vegetabiliskt)','t','f','f','f',0, null, '. Vattenolösliga maten färg som färg varierar från gul till orange, beroende på lösningsmedlet som används för utvinning','t', null, null, 'beta-karoten','beta-Carotene','E160a','Natur färgade isolerad från flera anläggningar, men det erhålls kommersiellt från morötter . Karotener förekomma som isomerer, bestående av en serie av kemiskt identiska, men steriskt olika komponenter. Sammansättningen skiljer sig åt mellan de växtarter.', null, 'Inga biverkningar är kända i de koncentrationer som används i livsmedel. Höga koncentrationer kommer att resultera i gul missfärgning av huden. Som karoten är en källa till vitamin A (2 delar karoten lika 1 del vitamin A), kommer höga koncentrationer orsaka symptom av vitamin A toxicitet.','f');
