INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98367,'t-2, 4-hexadiensyra','t','f','f','f',1,'allergi, astma','Sorbinsyra är ett konserveringsmedel, främst mot svampar och jästsvampar. Det är inte effektiv mot bakterier. Optimal aktivitet vid pH-värden under 6,5 (sur och något sura livsmedel).','f', null, null, 'Sorbinsyra','Sorbic acid','E200','Förekommer sorbinsyra naturligt i frukten av det europeiska rönn (sorbus aucuparia), varefter syran heter. Det är kommersiellt produceras av flera olika kemiska vägar.', null, 'Inga biverkningar i de koncentrationer som används. Endast en mycket liten andel människor har svaga pseudo-allergiska reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98370,'t-2 4-hexadiensyra potassiumsalt, 2-propenylacrylic syra potassiumsalt','t','f','f','f',0, null, 'Sorbinsyra är ett konserveringsmedel, främst mot svampar och jästsvampar. Det är inte effektiv mot bakterier. Optimal aktivitet vid pH-värden under 6,5 (surt och något sura livsmedel).','f', null, null, 'Kaliumsorbat','Potassium sorbate','E202','Kaliumsalt av sorbinsyra (e200), som förekommer naturligt i frukten av det europeiska rönn (sorbus aucuparia), varefter syran heter. Det är kommersiellt produceras av flera olika kemiska vägar.', null, 'Inga biverkningar i de koncentrationer som används. Endast en mycket liten andel människor har svaga pseudo-allergiska reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98371,'t-2 4-hexadiensyra kalciumsalt, 2-propenylacrylic-kalciumsalt','t','f','f','f',0, null, 'Sorbinsyra är ett konserveringsmedel, främst mot svampar och jästsvampar. Det är inte effektiv mot bakterier. Optimal aktivitet är vid pH-värden under 6,5 (syra och något sura livsmedel). Kalciumsorbat används främst i mjölkprodukter, dess verksamhet är liknande den för sorbinsyra','f', null, null, 'Kalciumsorbat','Calcium sorbate','E203','Kalciumsalt av sorbinsyra (e200), som förekommer naturligt i frukten av den europeiska mountain. -aska (sorbus aucuparia), varefter syran heter. Det är kommersiellt produceras av flera olika kemiska vägar.', null, 'Inga biverkningar i de koncentrationer som används. Endast en mycket liten andel människor har svaga pseudo-allergiska reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98372,'Bensoesyra, bensenkarboxylsyra, fenylkarboxylsyra','t','f','f','f',1,'allergi, astma','Bensoesyra och bensoater används som konserveringsmedel mot både jäst och svamp i sura produkter. De är inte särskilt effektivt mot bakterier och ineffektiva produkter med ett pH-värde över 5 (svagt surt eller neutralt). Höga koncentrationer resulterar i en syrlig smak, vilket begränsar tillämpningen. Bensoater ofta föredras, på grund av bättre löslighet. Är vanligt förekommande i de flesta frukter, särskilt bär','f', null, null, 'bensoesyra','Benzoic acid','E210','Bensoesyra, bensoater och bensoesyra estrar. Tranbär är en mycket rik källa av bensoesyra. Förutom frukt, bensoater förekommer naturligt i svampar, kanel, kryddnejlika och en del mjölkprodukter (på grund av bakteriell jäsning). För kommersiella ändamål är det framställas kemiskt från toluen', null, 'Inga biverkningar i de koncentrationer som används. Hos vissa personer bensoesyra och bensoater kan frigöra histamin och därmed orsaka pseudo-allergiska reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98373,'Bensenkarboxylsyra natriumsalt, dracylic-natriumsalt, fenylkarboxylsyra natriumsalt','t','f','f','f',2,'allergi, astma, hyperaktivitet, cancer, mutagenicitet','Bensoesyra och bensoater används som konserveringsmedel mot både jäst och bakterier i sura produkter. De är inte särskilt effektiva mot svamp, och ineffektivt i produkter med ett pH-värde över 5 (svagt surt eller neutralt). Höga koncentrationer har en sur smak, vilket begränsar tillämpningen. Bensoater ofta föredras, på grund av bättre löslighet.','f', null, null, 'Natriumbensoat','Sodium benzoate','E211','Bensoesyra, bensoater och bensoesyraestrar är vanligt förekommande i de flesta frukter, särskilt bär. Tranbär är en mycket rik källa av bensoesyra. Förutom frukt, bensoater förekommer naturligt i svampar, kanel, kryddnejlika och en del mejeriprodukter (som en följd av bakteriell jäsning). För kommersiella ändamål är det framställas kemiskt från toluen. En reaktion med c-vitamin utsöndrar bensen, som är en känd carcinogen.', null, 'Inga biverkningar i de använda koncentrationer. Hos vissa personer bensoesyra och bensoater kan frigöra histamin och därmed orsaka pseudo-allergiska reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98374,'Kaliumbensoat, kaliumsalt av bensenkarboxylsyra, kaliumsalt av fenylkarboxylsyra, bensenkarboxylsyra kaliumsalt, dracylic-kaliumsalt, fenylkarboxylsyra kaliumsalt','t','f','f','f',1,'allergi, astma','Bensoesyra och bensoater används som konserveringsmedel mot både jäst och bakterier i sura produkter. De är inte särskilt effektiva mot svamp, och ineffektivt i produkter med ett pH-värde över 5 (svagt surt eller neutralt). Höga koncentrationer leder till en sur smak, vilket begränsar tillämpningen. Bensoater ofta föredras, på grund av bättre löslighet. Är vanligt förekommande i de flesta frukter, särskilt bär','f', null, null, 'kaliumbensoat','Potassium benzoate','E212','Bensoesyra, bensoater och bensoesyra estrar. Tranbär är en mycket rik källa av bensoesyra. Förutom frukt, bensoater förekommer naturligt i svampar, kanel, kryddnejlika och en del mejeriprodukter (som en följd av bakteriell jäsning). För kommersiella ändamål är det framställas kemiskt från toluen.', null, 'Inga biverkningar i de koncentrationer som används. Hos vissa personer bensoesyra och bensoater kan frigöra histamin och därmed orsaka pseudo-allergiska reaktioner.','f');
