INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98386,'Kaliummetabisulfit','t','f','f','f',1,'allergi, astma, migrän','Det är ett vitt, icke-stabilt pulver, vilket reagerar med syre för att bilda kaliumsulfat. Under sura betingelser bildar den svavelsyrlighet, som verkar som ett konserveringsmedel. Det används som ett konserveringsmedel i frukter och fruktprodukter och kan användas som ett blekmedel.','f', null, null, 'Kaliummetabisulfit','Potassium metabisulphite','E224','Kaliumsalt av svavelsyrlighet. På grund av sin oxidativa effekter svaveldioxid kan förstöra en del av vitaminer i produkter det används. Det är att försämras till oskadliga sulfat i levern och därefter utsöndras med urinen. Människor som inte tål naturliga sulfiter bör också undvika de tillsatta dem (e221-228). Det lindrar baksmälla.', null, 'På grund av dess oxiderande effekt kan det minska vitaminhalten i produkter. Det reduceras i levern till ofarliga sulfat och utsöndras i urinen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98388,'Kalciumsulfit','t','t','t','t',3,'allergi, astma, migrän','Det är ett vitt, icke-stabilt pulver, vilket reagerar med syre för att bilda kalciumsulfat. Under sura betingelser bildar den svavelsyrlighet, som verkar som ett konserveringsmedel. Det används som ett konserveringsmedel och kan användas som ett blekmedel i sockerproduktion. Den ökar fastheten hos burk (på grund av kalcium).','f', null, null, 'Calcium sulfit','Calcium sulphite','E226','Kalciumsalt av svavelsyrlighet.', null, ' Grund av dess oxiderande verkan, kan det minska den vitamininnehåll i produkter. Den reduceras i levern till ofarliga sulfat och utsöndras i urinen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98389,'Kalciummetabisulfit, kalciumbisulfit','t','t','t','t',3,'allergi, astma, migrän','Det är en grönaktig vätska, som reagerar för att bilda kalciumsulfit. Under sura betingelser bildar den svavelsyrlighet, som verkar som ett konserveringsmedel. Det används som ett konserveringsmedel i öl. Det ökar fasthet konserverade grönsaker (på grund av kalcium).','f', null, null, 'Calcium vätgas sulfit','Calcium hydrogen sulphite','E227','Kalciumsalt av svavelsyrlighet.', null, 'Är det minskas i levern till ofarliga sulfat och utsöndras i urinen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98390,'Kalium väte sulfit, kaliumbisulfit','t','f','f','f',1,'allergi, astma, migrän','Det är ett vitt, icke-stabilt pulver, vilket reagerar med syre för att bilda kaliumsulfat. Under sura betingelser bildar den svavelsyrlighet, som verkar som ett konserveringsmedel.','f', null, null, 'Kaliumväte sulfit','Potassium hydrogen sulphite','E228','Kaliumsalt av svavelsyrlighet. Det är att försämras till oskadliga sulfat i levern och därefter utsöndras med urinen. Människor som inte tål naturliga sulfiter bör också undvika de tillsatta dem (e221-228). Denna tillsats stör grupp b-vitamin intag.', null, 'Är det minskas i levern till ofarliga sulfat och utsöndras i urinen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98391,'Bifenyl, fenylbensen','t','t','t','t',3,'allergi, astma','Vit, vatten olösligt pulver som används främst mot Penicilliumfungi växer på citrusfrukter. Den används för att desinficera behållare och i impregnering inslagning av citrusfrukter. Ibland frukter doppas i en lösning av difenyl, som långsamt penetrerar skalet och kan finnas i själva frukten.','f', null, null, 'Diphenyl','Diphenyl','E230','Syntetiserade från bensen.', null, 'Det utsöndras oförändrat med njurarna. Känslighet har rapporterats för människor som hanterar stora sändningar av citrusfrukter (fartyg, lastbilar). I övrigt finns inga biverkningar','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98392,'Orthoxenol, ortho-fenylfenol, dowicide 1','t','t','t','t',3,'allergi, astma','Vit, vatten olösligt pulver som används i synnerhet mot Penicilliumfungi växer på citrusfrukter, äpplen och päron. Det långsamt tränger skalet och kan finnas i själva frukten.','f', null, null, 'O-Phenylphenol','o-Phenylphenol','E231','Syntetiserade från fenyl eter.', null, 'Det utsöndras oförändrat via njurarna utan biverkningar.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98393,'Natrium orthophenylphenolate, dowicide a','t','f','f','f',1,'allergi, astma','Vitt pulver som används speciellt mot Penicilliumfungi växer på citrusfrukter, äpplen och päron. Det är mycket lösligt i vatten, och användas för sprutning eller doppning frukter. Den långsamt penetrerar huden och kan vara närvarande i själva frukten.','f', null, null, 'Natrium o-fenylfenol','Sodium o-Phenylphenol','E232','Natriumsalt av fenylfenol, syntetiserad från fenyleter.', null, ' Normal tvättning avlägsnar den lätt. Intagna rester utsöndras oförändrat via njurarna och provocera inga negativa reaktioner.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98394,'Tiabendazol','t','t','t','f',3, null, 'En allmänt använd fungicid, särskilt på frukt. Den är huvudsakligen sprutas på frukter, det kan också vara en komponent av en vattenhaltig lösning, i vilken frukten är nedsänkta. Det kan endast användas på den yttre ytan av frukter.','f', null, null, 'Tiabendazol','Thiabendazole','E233','Synthetic fungicid', null, 'är det hydrolyseras i levern och utsöndras via njurarna. Inga biverkningar vid de angivna acceptabla halter.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98395,'Nisin beredning','t','f','f','f',1,'allergi, astma, migrän','Används som konserveringsmedel mot grampositiva förruttnelsebakterier','f', null, null, 'Nisin','Nisin','E234','Nisin är en polypeptid (kort protein) antibiotikum som produceras av de lactis bakterien lactococcus, som normalt används vid ostframställning.', null, 'eftersom det är ett protein, behandlas det av kroppen som sådan, och digererades i tunntarmen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98400,'Hexametylentetramin, hexamin, metenamin','t','t','t','t',3,'allergi, astma','Används som konserveringsmedel mot svampar.','f', null, null, 'Hexametylentetramin','Hexamethylenetetramine','E239','Kommersiellt syntetiseras från formaldehyd och ammoniak. Behandlas av levern och utsöndras därefter. Höga halter orsaka betydande biverkningar,', null, 'är det metaboliseras i levern och utsöndras. Höga koncentrationer har betydande biverkningar, emellertid är dessa koncentrationer nådde aldrig i livsmedel, på grund av smaken den förlänar','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98402,'Dimetyl pyrokarbonat, dmdc','t','f','f','f',1,'allergi, astma','Det används som ett konserveringsmedel (kall-sterilisering) i drycker.','f', null, null, 'Dimetyl-dikarbonat','Dimethyl dicarbonate','E242','Syntetiseras kemiskt.', null, 'None. ','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98403, null, 't','f','f','f',2,'allergi, astma, migrän, cancer','Vitt pulver som används som konserveringsmedel mot <a href=''../../uk/bact/clbot.htm''>Clostridium botulinum</a> (en bakterie som kan orsaka botulism) i köttprodukter.','f', null, null, 'Kaliumnitrit','Potassium nitrite','E249','Naturligt förekommande mineral. Kan brytas eller produceras kemiskt från kaliumnitrat.', null, 'Nitriter är prekursorer till (eventuellt karcinogena) nitrosaminer, vilka är formade i magsäcken från nitriter och proteiner. Höga halter kan reagera med hemoglobin. Det är inte tillåtet i produkter avsedda för barn under 1 år. Små barn har en annan typ av hemoglobin, som är mycket mer reaktiv gentemot nitrit än normalt hemoglobin.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98404, null, 't','f','f','f',2,'allergi, astma, migrän, cancer','Vitt pulver som används som konserveringsmedel mot <a href=''../../uk/bact/clbot.htm''>Clostridium botulinum</a> (en bakterie som kan orsaka botulism) i köttprodukter.','f', null, null, 'Natriumnitrit','Sodium nitrite','E250','Naturligt förekommande mineral. Kan brytas eller produceras kemiskt från natriumnitrat. Nitriter är prekursorer av nitrosaminer (som kan vara cancerframkallande), som bildas i magen från nitriter och proteiner. Höga halter kan reagera med hemoglobin. Nitrit är inte tillåtna i produkter avsedda för barn under 6 månaders ålder. Välkända cancerframkallande, nitrosaminer, form vid upphettning över öppen eld.', null, 'Nitrit är prekursorer av (möjligen cancerframkallande) nitrosaminer, som bildas i magsäcken från nitrit och proteiner. Höga halter kan reagera med hemoglobin. Det är inte tillåtet i produkter avsedda för barn under 6 månader. Små barn har en annan typ av hemoglobin, som är mycket mer reaktiv gentemot nitrit än normalt hemoglobin.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98405,'Chilesalpeter, kubisk eller soda salpeter','t','f','f','f',1,'allergi, astma, migrän','Vitt pulver används som ett konserveringsmedel och mot fädning av naturliga färger.','f', null, null, 'Natriumnitrat','Sodium nitrate','E251','Naturligt förekommande mineral. Också i nästan alla grönsaker. Vid uppvärmning eller passage till magsäcken förvandlas till nitrit (e250)', null, 'Nitrat har inga biverkningar, men kan de omvandlas till nitrit (<a href=''e250.htm''>E250</a>) antingen genom upphettning eller när den förekommer i magen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98406,'Saltpeter','t','f','f','f',1,'allergi, astma, migrän','Vitt pulver används som ett konserveringsmedel och mot fädning av naturliga färger.','f', null, null, 'Kaliumnitrat','Potassium nitrate','E252','Naturligt förekommande mineral. Också närvarande i nästan alla grönsaker.', null, 'Nitrat har inga biverkningar, men genom uppvärmning eller i magen kan det omvandlas till nitrit (<a href=''e250.htm''>E250</a> ).','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98407,'Ethanoic acid, glacial','t','f','f','f',0, null, 'Ättiksyra används som ett konserveringsmedel mot bakterier och svampar. I majonnäs är det tillsätts för att öka inaktiveringen av <a href=''../../uk/bact/salmon.htm''>Salmonella</a>. Den högsta aktiviteten är vid lågt pH. Den kan också användas som en buffert i sura livsmedel. Den används också som en doft komponent.','f', null, null, 'Ättiksyra','Acetic acid','E260','Natural syra, som finns i de flesta frukter. Producerad av bakteriell jäsning och därmed i alla jästa produkter. Kommersiellt framställs genom bakteriell jäsning av socker, melass eller alkohol eller genom kemisk syntes från acetealdehyde.', null, 'Inga biverkningar, det är en normal komponent i alla kroppens celler. Bör endast undvikas av människor som har en (mycket sällsynt) vinäger intolerans.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98408, null, 't','f','f','f',0, null, 'Acetates are used as preservatives and buffers.','f', null, null, 'Potassium acetate','Potassium acetate','E261','Potassium salt of acetic acid, an natural acid, present in most fruits. Produced by bacterial fermentation and thus present in all fermented products. Commercially produced by bacterial fermentation of sugar, molasses or alcohol or by chemical synthesis from acetealdehyde. Should be avoided the people who can not tolerate vinegar.', null, 'No side effects, acetates are a normal component of all body cells. Should only be avoided by people who have a (very rare) vinegar intolerance.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98409,'Natriumacetat, natriumdiacetat','t','f','f','f',0, null, 'Acetater används som konserveringsmedel och buffertar.','f', null, null, 'Natriumacetat','Sodium acetate','E262','Natriumsalt av ättiksyra, en naturlig syra, som finns i de flesta frukter. Producerad av bakteriell jäsning och därmed i alla jästa produkter. Kommersiellt genom bakteriell fermentation av socker, melass eller alkohol eller genom kemisk syntes från acetealdehyde. Bör undvikas de människor som inte tål ättika.', null, 'Inga biverkningar, acetat är en normal del av alla kroppens celler. Bör endast undvikas av människor som har en (mycket sällsynt) vinäger intolerans.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98412, null, 't','f','f','f',0, null, 'Acetater används som konserveringsmedel och buffertar. Kalciumacetat används speciellt mot vissa sporbildande organismer i bröd.','f', null, null, 'Kalciumacetat','Calcium acetate','E263','Kalciumsalt av ättiksyra, en naturlig syra, som finns i de flesta frukter. Producerad av bakteriell jäsning och därmed i alla jästa produkter. Kommersiellt framställs genom bakteriell jäsning av socker, melass eller alkohol eller genom kemisk syntes från acetealdehyde.', null, 'Inga biverkningar, acetat är en normal del av alla kroppens celler. Bör endast undvikas av människor som har en (mycket sällsynt) vinäger intolerans.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98413, null, 't','f','f','f',0, null, 'Mjölksyra och laktat används som konserveringsmedel, främst mot jäst och svampar. Den används också för att öka stabiliteten av potatisprodukter, ökar och stabiliserar anti-oxidanter och pektiner.','t', null, null, 'Mjölksyra','Lactic acid','E270','Natural syra som produceras av bakterier i fermenterade livsmedel. Alla fermenterade livsmedel är mycket rik på mjölksyra. Kommersiellt genom bakteriell fermentation på stärkelse och melass. Produceras också i stora mängder i tjocktarmen av de inhemska bakterierna.', null, 'Inga biverkningar hos vuxna. D-eller DL-laktat (stereoisomerer) bör inte ges till spädbarn och små barn, eftersom de ännu inte har utvecklat lämpliga enzymer i levern att metabolisera dessa former av laktat.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98414,'Ethylformic syra, metylättiksyra, propansyra','t','f','f','f',1,'allergi, astma','Propionsyra och propionater används som konserveringsmedel, främst mot svamp. Den används ofta i bakverk mot bakteriell nedbrytning. På grund av den starka lukten den är begränsad i tillämpningar.','t', null, null, 'Propionsyra','Propionic acid','E280','Natural syra som finns i små mängder i många livsmedel, ibland i höga koncentrationer som produceras av bakterier i fermenterade livsmedel, såsom olika typer av schweizerost . Det är också produceras i stor skala av bakterierna i tjocktarmen. Det är också en normal komponent i svett.', null, 'Inga biverkningar. Propionat är en normal liten kedja fettsyra och används i många metaboliska vägar i kroppen.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98415,'Natrium propanoat','t','f','f','f',1,'allergi, astma','Propionsyra och propionater används som konserveringsmedel, främst mot svamp.','t', null, null, 'Natriumpropionat','Sodium propionate','E281','Natriumsalt av propionsyra, en naturlig syra som finns i små mängder i många livsmedel, ibland i höga koncentrationer som produceras av bakterier i fermenterade livsmedel, såsom olika typer av schweizerost. Det är också produceras i stor skala av bakterierna i tjocktarmen.', null, 'Inga biverkningar. Propionat är en normal liten kedja fettsyra och används i många metaboliska vägar i kroppen.','f');
