INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98418, null, 't','f','f','f',1,'allergi, astma','Borsyra och borater används som en buffert för att kontrollera surheten i produkter. Den används också i multi-vitamin/mineral produkterna som en mineralkälla.','f', null, null, 'Borsyra','Boric acid','E284','Natural syra, men kommersiellt från bor.', null, 'Inga biverkningar som är kända från livsmedel. I farmaceutical förberedelser Halterna är mycket högre och kan orsaka flera biverkningar.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98419,'Borax','t','f','f','f',1,'allergi, astma, metabola sjukdomar','Borsyra och borater används som en buffert för att kontrollera surheten i produkter. Den används också i multi-vitamin/mineral produkterna som en mineralkälla.,','f', null, null, 'Natriumtetraborat','Sodium tetraborate','E285','Natriumsalt av borsyra, en naturlig sur men kommersiellt från bor.', null, ' Inga biverkningar är kända från livsmedel. I farmaceutical förberedelser Halterna är mycket högre och kan orsaka flera biverkningar.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98420, null, 't','f','f','f',0, null, 'Används i kolsyrade drycker för den glittrande effekt. Används också i modifierad atmosfär förpackning och drivmedel i gasbehållare.','f', null, null, 'Koldioxid','Carbon dioxide','E290','Normal naturgas. En del av luft och produceras av ämnesomsättningen i kroppen.', null, 'Inga biverkningar är kända från livsmedel.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98421,'2-hydroxibutandisyra, äppelsyra','t','f','f','f',0, null, 'Bilar som syra, smak förening och färg stabilisator i äpple-och grapejuice.','t', null, null, 'DL-äppelsyra','DL-Malic Acid','E296','Äppelsyra en naturlig syra som finns i de flesta frukter och många grönsaker. Kommersiellt göras genom kemisk syntes. Det är en del av den metaboliska vägen för varje levande cell.', null, 'Inga biverkningar kända. Höga halter är inte tillåtna i barnmat, eftersom små barn saknar förmåga att metabolisera stora mängder äppelsyra.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98422, null, 't','f','f','f',0, null, 'Används som syra och struktur stabilisator i ett stort utbud av produkter. Också användas som syrakälla i bakpulver','t', null, null, 'Fumarsyra','Fumarsyra','E297','Är fumarsyra en naturlig syra närvarande i många frukter och grönsaker. Kommersiellt göras genom fermentation av socker med svampar eller genom kemisk syntes. Det är en del av metabolism i varje levande cell.', null, 'Nej biverkningar kända effekter','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98423,'Vitamin c','t','f','f','f',0, null, 'Askorbinsyra är C-vitamin, men det kan inte läggas till som ett vitamintillskott när de är märkta E300. Vid tillsats i livsmedel den fungerar som en antioxidant och bröd förstärkare.','t', null, null, 'Askorbinsyra','Ascorbic acid','E300','A cid, som förekommer naturligt i de flesta frukter och grönsaker. Kommersiellt syntetiseras genom bakteriell fermentering av glukos, följt av kemisk oxidation.', null, 'Inga biverkningar som är kända i de använda koncentrationerna.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98424,'Askorbinsyra natriumsalt','t','f','f','f',0, null, 'Anti-oxidant och bröd förstärkare. Det förhindrar brunfärgning av frukt samt bildandet av nitrosaminer i kött. Askorbinsyra är vitamin C, kan dock inte läggas till som ett vitamintillskott när de är märkta E300','t', null, null, 'Natriumaskorbat','Sodium ascorbate','E301','Natriumsalt av askorbinsyra (vitamin c, en atural syra), som förekommer naturligt i. flesta frukterna och grönsakerna. Kommersiellt syntetiseras genom bakteriell fermentering av glukos, följt av kemisk oxidation.', null, 'Inga biverkningar som är kända i de använda koncentrationerna.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98425,'Askorbinsyra kalciumsalt, kalciumaskorbat dihydrat, kalciumsalt av 2,3-didehydro-l-treo-hexono1,4-lakton dehydratisera','t','f','f','f',0, null, 'Anti-oxidant och bröd förstärkare. Det förhindrar brunfärgning av frukt samt bildandet av nitrosaminer i kött. Askorbinsyra är vitamin C, kan dock inte läggas till som ett vitamintillskott när de är märkta E302','t', null, null, 'Kalciumaskorbat','Calcium ascorbate','E302','Kalciumsalt av askorbinsyra (vitamin c, en atural syra), som förekommer naturligt i. flesta frukterna och grönsakerna. Kommersiellt syntetiseras genom bakteriell fermentering av glukos, följt av kemisk oxidation.', null, 'Inga biverkningar som är kända i de använda koncentrationerna.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98426,'Vitamin c-palmitat, askorbylstearat','t','f','f','f',0, null, 'Anti-oxidant i feta produkter, särskilt till att förhindra härskning i vegetabiliska oljor med många omättade fettsyror. Används även för de färgämnen E160 och E161 för att förhindra oxidation av dessa färger.','t', null, null, 'Askorbylpalmitat','Ascorbyl Palmitate','E304','Kombinationen av fettsyran palmitat med askorbinsyra (vitamin c, en atural syra), som förekommer naturligt i flesta frukterna och grönsakerna. Palmitinsyra produceras från fett (för askorbat se e300).', null, 'Inga biverkningar är kända i de använda koncentrationer.','t');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98427,'Vitamin e koncentrera sig','t','f','f','f',0, null, 'Anti-oxidant i feta produkter, särskilt tillsätts för att förhindra härskning av animaliska oljor. Vitamin eActivity är begränsad. Tillämpningar är begränsade på grund av den starka smak den förlänar.','f', null, null, 'Tokoferol-rika extrakt','Tocopherol-rich extract','E306','Extrakt av vegetabiliska oljor som är rika på tokoferoler (vitamin e). Höga koncentrationer av vitamin e kan orsaka biverkningar. Detta gäller för användning av tokoferol som vitamintillskott, dock inte som ett tillägg för mat.', null, 'Vitamin Ecan orsaka flera biverkningar i höga koncentrationer. Dessa är inte förknippade med användningen av tokoferoler som tillsatser, men som vitamintillskott.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98428,'Vitamin e, dl-alfa-tokoferol, (+)-alfa-tokoferol, rrr-a-tokoferol, 5,7,8-trimethyltocol, d-alfa-tokoferol koncentrat, tocoferolkoncentrat','t','f','f','f',0, null, 'Anti-oxidant i feta produkter, särskilt tillsätts för att förhindra härskning av animaliska oljor. Hög vitamin eActivity. Tillämpningar är begränsade på grund av den starka smak den förlänar.','f', null, null, 'Alfa-tokoferol','Alpha-tocopherol','E307','Extrakt av vegetabiliska oljor som är rika på tokoferoler (vitamin e), i synnerhet solrosolja. Kan också framställas genom kemisk syntes. Det kan också produceras genom kemisk syntes. Användningen av höga koncentrationer av vitamin e kan orsaka biverkningar. Detta gäller för användning av tokoferol som vitamintillskott, dock inte som ett tillägg för mat.', null, 'Vitamin Ecan orsaka flera biverkningar i höga koncentrationer. Dessa är inte förknippade med användningen av tokoferoler som tillsatser, men som vitamintillskott.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98431,'Vitamin e','t','f','f','f',0, null, 'Anti-oxidant i feta produkter, särskilt tillsätts för att förhindra härskning av olivolja. Ganska låg vitamin eActivity.','f', null, null, 'Gamma-tokoferol','Gamma-tocopherol','E308','Utdrag ur vegetabiliska oljor som är rika på tokoferoler (vitamin e), särskilt sojaolja. Kan också göras genom kemisk syntes.', null, 'Vitamin Ecan orsaka flera biverkningar i höga koncentrationer. Dessa är inte förknippade med användningen av tokoferoler som tillsatser, men som vitamintillskott.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98432,'Vitamin e','t','f','f','f',0, null, 'Anti-oxidant i feta produkter, särskilt tillsätts för att förhindra härskning av olivolja. Ganska låg vitamin eActivity. Tillämpningar är begränsade på grund av den starka smak den förlänar.','f', null, null, 'Delta-tokoferol','Delta-tocopherol','E309','Extrakt av vegetabiliska oljor som är rika på tokoferoler (vitamin e), speciellt sojabönolja. Kan också framställas genom kemisk syntes. Det kan även framställas genom kemisk syntes.', null, 'Vitamin Ecan orsaka flera biverkningar i höga koncentrationer. Dessa är inte förknippade med användningen av tokoferoler som tillsatser, men som vitamintillskott.','f');
