INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98537,'Pectins','t','f','f','f',0,'interstinal problem, flatulens', null, 'f', null, null, 'Pektin','Pectin','E440','Natural sura polysackarid som finns i nästan alla frukter, särskilt äpplen, kvitten, apelsiner. Kommersiellt producerade från Apple massa och apelsinskal. Natrium-, kalium-och ammonium-pektater är salter av respektive pektiner. Amiderat pektin erhålls genom behandling av pektin med ammoniak som resulterar i bildandet av amider i syra-kedjan. Pektiner fungera som förtjockningsmedel, och därför höga halter kan orsaka tarmproblem. På grund av dess förtjockande effekt, kan de användas i kosten, eftersom det minskar svält. Pektiner kan fermenteras i tjocktarmen, som orsakar gasbildning.', null, null, 'f');
