INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98540,'Kalcium diväte difosfat, Dikalciumfosfat Pyrofosfat, tetrakaliumpyrofosfat, tetranatriumpyrofosfat, Trinatrium DiphosphateAcid kalciumpyrofosfat, monokalciumfosfat diväte-fosfat, kalciumpyrofosfat, surt natriumpyrofosfat, dinatriumdivätepyrofosfat, Dinatrium diväte difosfat, kaliumpyrofosfat, tetrakalium-difosfat, natriumpyrofosfat, Tetranatrium difosfat, Acid trinatrium-fosfat, monoväte trinatrium difosfat','t','f','f','f',1,'allergi, astma, hyperaktivitet, metaboliska sjukdomar','Buffertar och emulgeringsmedel. E450 (iii) binder också metaller och förhindrar missfärgning på grund av metaller. E450 (VI) används också som bröd förstärkare och kalciumtillägg..','f', null, null, 'Dinatriumpyrofosfat','Disodium Pyrophosphate','E450','Salter av natrium / kalium / kalcium med fosfater Alla framställs syntetiskt från de respektive karbonater och fosforsyra. Höga halter kan störa vissa metaboliska processer, eftersom fosfater är viktiga för ämnesomsättningen.', null, 'Inga kända vid användning i livsmedel. Höga koncentrationer av fosfater kan störa flera metaboliska processer som fosfat spelar en viktig roll i allmänhet metabolism.','f');
INSERT INTO e (id, aliases, approved, bannedinaustralia, bannedincanada, bannedinusa, category, diseases, function, gmo, linksbanned, linksdiseases, name, nameenglish, number, origin, properties, sideeffect, vegan) VALUES(98549,'Cellulosagel, pulvriserad cellulosa, kristallin cellulosa, cellulosa pulver','t','f','f','f',0,'diarré, flatulens, förstoppning','Många olika användningsområden, främst som förtjockningsmedel, men också som fyllmedel, kostfiber, anti klumpar agent och emulsfier.','f', null, null, 'Mikrokristallin cellulosa','Microcrystalline Cellulose','E460','Cellulosa är den viktigaste polysackariden och beståndsdel i trä och alla strukturer växter. Kommersiellt framställd av trä. Cellulosa är olöslig, men kan fermenteras i grovtarmen. Höga koncentrationer kan orsaka tarmproblem. Därför kan cellulosa inte användas i barnmat.', null, ' Cellulosa är icke löslig, men kan fermenteras i grovtarmen. Stora koncentrationer kan orsaka tarmproblem, såsom uppblåsthet, förstoppning och diarré. På grund av detta faktum inte kan användas i avvänjningskost.','f');
